$(function(){$("#selectBtn").on("change",function(){$("#newsGallery").empty(),$("header").css();var e=event.target.value;$("#loadingAjax").show(),$.ajax({method:"GET",url:"https://api.nytimes.com/svc/topstories/v2/"+e+".json?"+$.param({"api-key":"b76abdc620b044a8aeec75d713dcdb0f"})}).done(function(e){if(e.results){var t=0;$.each(e.results,function(e,a){a.multimedia[4]&&t<12&&(t++,$("#newsGallery").append('<a href="'+a.url+'" target="_blank"><li style="background-image:url('+a.multimedia[4].url+')"><p>'+a.title+"</p></li></a>")),$("#loadingAjax").hide()})}else $("#selectBox").append("sorry no news today!")})})});